class Solution {
public:
    bool check7(vector<int> &res) {
        int rem = 0;
        for (int i = 0; i < res.size(); i++) {
            rem = (rem * 10 + res[i]) % 7;
        }
        return rem == 0;
    }
    string largestPalindrome(int n, int k) {
        vector<int> res(n, 9);
        if (k == 2) {
            res[0] = 8;
            res[n-1] = 8;
        } else if (k == 4) {
            res[0] = 8;
            res[n-1] = 8;
            if (n > 1) {
                res[1] = 8;
                res[n-2] = 8;
            }
        } else if (k == 5) {
            res[0] = 5;
            res[n-1] = 5;
        } else if (k == 6) {

            if (n == 1) {
                res[0] = 6;
            } else if (n == 2) {
                res[0] = 6;
                res[1] = 6;
            } else {
                res[0] = 8;
                res[n-1] = 8;
                if (n % 2 == 0) {
                    res[n/2-1] = 7;
                    res[n/2] = 7;
                } else {
                    res[n/2] = 8;
                }
            }
        } else if (k == 7) {
            if (n % 2 == 0) {
                for (int i = 9; i >=3; i--) {
                    res[n/2-1] = i;
                    res[n/2] = i;
                    if (check7(res)) break;
                }
            } else {
                for (int i = 9; i >= 3; i--) {
                    res[n/2] = i;
                    if (check7(res)) break;
                }
            }

        } else if (k == 8) {
            for (int i = 0; i < 3; i++) {
                if (i < n) res[i] = 8;
                if (n-1-i >= 0) res[n-1-i] = 8;
            }

        }
        vector<char> s(n);
        for (int i = 0; i < n; i++) s[i] = res[i] + '0';
        return string(s.begin(),s.end());
    }
};
