class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        if k == 2:
            if n >= 2:
                return '8' + '9' * (n - 2) + '8'
            return '8'
        if k == 4:
            if n >= 4:
                return '88' + '9' * (n - 4) + '88'
            return '8' * n
        if k == 5:
            if n >= 2:
                return '5' + '9' * (n - 2) + '5'
            return '5'
        if k == 6:
            if n >= 3:
                if n % 2:
                    rem = (16 + 9 * (n - 3)) % 3
                    m = (n - 3) // 2
                    return '8' + '9' * m + str(9 - rem) + '9' * m + '8'
                rem = (16 + 9 * (n - 4)) % 3
                d = 9
                if rem == 1:
                    d = 7
                elif rem == 2:
                    d = 8
                m = (n - 4) // 2
                return '8' + '9' * m + str(d) * 2 + '9' * m + '8'
            return '6' * n
        if k == 7:
            if n >= 3:
                mult, dupe = pow(10, n // 2, 7), 1
                if n % 2 == 0:
                    mult = (mult + pow(10, n // 2 - 1)) % 7
                    dupe = 2
                k = ((pow(10, n, 7) - 1) * pow(mult, -1, 7)) % 7
                d = 9 - k
                m = (n - 1) // 2
                return '9' * m + str(d) * dupe + '9' * m
            return '7' * n
        if k == 8:
            if n >= 6:
                return '888' + '9' * (n - 6) + '888'
            return '8' * n
