class Solution {
public:

int isdivisible7(string num)
{
    int n = num.length(), gSum=0;
    if (n == 0)
        return 1;

    // Append required 0s at the beginning.
    if (n % 3 == 1) {
        num="00" + num;
        n += 2;
    }
    else if (n % 3 == 2) {
        num= "0" + num;
        n++;
    }

    // add digits in group of three in gSum
    int i, GSum = 0, p = 1;
    for (i = n - 1; i >= 0; i--) {

        // group saves 3-digit group
        int group = 0;
        group += num[i--] - '0';
        group += (num[i--] - '0') * 10;
        group += (num[i] - '0') * 100;

        gSum = gSum + group * p;

        // generate alternate series of plus
        // and minus
        p *= (-1);
    }

    return (gSum % 7 == 0);
}
    bool check(string str)
{
    int n = str.length();

    // Empty string
    if (n == 0)
        return false;

    // If there is single digit
    if (n == 1)
        return ((str[0]-'0')%8 == 0);

    // If there is double digit
    if (n == 2)
        return (((str[n-2]-'0')*10 + (str[n-1]-'0'))%8 == 0);

    // If number formed by last three digits is
    // divisible by 8.
    int last = str[n-1] - '0';
    int second_last = str[n-2] - '0';
    int third_last = str[n-3] - '0';

    return ((third_last*100 + second_last*10 + last) % 8 == 0);
}
    string largestPalindrome(int n, int k) {
        if(k==1)
        {
            string s(n,'9');
            return s;
        }
        if(k==2)
        {
            string s(n,'9');
            s[n-1]='8';
            s[0]='8';
            return s;
        }
        if(k==3)
        {
            string s(n,'9');
            return s;
        }
        if(k==4)
        {
            string s(n,'9');
            s[n-1]='8';
            s[0]='8';
            if(n>1)
            {
                s[1]='8';
                s[n-2]='8';
            }

            return s;
        }
        if(k==5)
        {
            string s(n,'9');
            s[n-1]='5';
            s[0]='5';
            return s;
        }
        if(k==6)
        {
            string s(n,'9');
           if(n==1)
           {
               s[0]='6';
           }
            else if(n==2)
            {
                s[0]='6';
                s[1]='6';
            }
            else
            {
                s[0]='8';
                s[n-1]='8';
                if(n%2==1)
                s[n/2]='8';
                else
                {
                    s[n/2]='7';
                    s[n/2-1]='7';
                }
            }
            return s;
        }
        if(k==7)
        {
            string s(n,'9');
            if(n%2==1)
            {
                for(char c='9';c>='0';c--)
                {
                    s[n/2]=c;
                    if(isdivisible7(s))
                        return s;

                }
            }
            else
            {
                 for(char c='9';c>='0';c--)
                {
                    s[n/2]=c;
                     s[n/2-1]=c;
                    if(isdivisible7(s))
                        return s;

                }
            }

        }
        if(k==8)
        {
            string s(n,'9');
            s[n-1]='8';
            s[0]='8';
            if(n==2)
            {
                s[1]='8';
                s[n-2]='8';
            }
            if(n>2)
            {
                s[1]='8';
                s[n-2]='8';
                s[n-3]='8';
                s[2]='8';
            }
                return s;
        }
        if(k==9)
        {
            string s(n,'9');
            return s;
        }
        return "0";
    }
};
