class Solution {
public:
    string largestPalindrome(int N, int K) {
        string res = string(N, '9');
        if (K == 1) {
            return res;
        }
        else if (K == 2) {
            res[0] = res[N - 1] = '8';
            return res;
        }
        else if (K == 3) {
            return res;
        }
        else if (K == 4) {
            res[0] = res[N - 1] = '8';
            if (N >= 3) res[1] = res[N - 2] = '8';
            return res;
        }
        else if (K == 5) {
            res[0] = res[N - 1] = '5';
            return res;
        }
        else if (K == 6) {
            if (N <= 2) return string(N, '6');
            res[0] = res[N - 1] = '8';
            int mid = (N - 1) / 2;
            if (N % 2 == 0) res[mid] = res[N - 1 - mid] = '7';
            else res[mid] = '8';
        }
        else if (K == 7) {
            if (N <= 2) return string(N, '7');
            int mid = (N - 1) / 2;
            for (char c = '9'; c >= '0'; c--) {
                res[mid] = res[N - 1 - mid] = c;
                int r = 0, p = 1;
                for (int i = N - 1; i >= 0; i--) {
                    int x = res[i] - '0';
                    r = (r + p * x % 7) % 7;
                    p = p * 10 % 7;
                }
                if (r == 0) return res;
            } 
        } 
        else if (K == 8) {
            res[0] = res[N - 1] = '8';
            if (N >= 3) res[1] = res[N - 2] = '8';
            if (N >= 5) res[2] = res[N - 3] = '8';
            return res;
        } 
        else if (K == 9) {
            return res;
        }
        return res;
    }
};
