class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return '9' * n
        if k == 2:
            if n == 1: return '8'
            elif n == 2: return '88'
            else:return '8' + '9' * (n - 2) + '8'
        if k == 3:
            return '9' * n
        if k == 4:
            if n == 1: return '8'
            elif n == 2: return '88'
            elif n == 3: return '888'
            else: return '88' + '9' * (n - 4) + '88'
        if k == 5:
            if n == 1:return '5'
            if n == 2: return '55'
            else: return '5' + '9' * (n-2) + '5'
        if k == 6:
            if n == 1: return '6'
            elif n == 2: return '66'
            return '8' + '9' * ((n-1)//2-1) + ('8' if n % 2 == 1 else '77') + '9' * ((n-1)//2-1) + '8'
        if k == 7:
            ans = ''
            r = 0
            j = 0
            for i in range((n-1) // 2):
                ans += '9'
                r = (r + 9*pow(10, i + 1, 7) + 9*pow(10, n - i, 7)) % 7
                j = i
            i = j
            if (n-1) // 2 >= 0:
                i += 1
            if n % 2 == 1:
                for j in range(9, -1, -1):

                    if (r + j*pow(10, i + 1, 7)) % 7 == 0:
                        return ans + str(j) + ans[::-1]
            if n % 2 == 0:
                for j in range(9, -1, -1):
                    if (r + j*pow(10, i + 1, 7)+ j*pow(10, n - i, 7)) % 7 == 0:
                        return ans + str(j) + str(j) + ans[::-1]
        if k == 8:
            if n <= 6:
                return '8' * n
            return '8' * 3 + '9' * (n - 6) + '8' * 3
        if k == 9:
            return '9' * n
