class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans = "";

        for (int i = 0; i < n; i++) {
            ans += '9';
        }

        if (k == 1 or k == 3 or k == 9) {
            return ans;
        }

        if (k == 5) {
            ans[n - 1] = '5';
            if (n > 1) {
                ans[0] = '5';
            }

            return ans;
        }

        if (k == 2) {
            ans[n - 1]--;
            if (n > 1) {
                ans[0]--;
            }

            return ans;
        }

        if (k == 4 or k == 8) {
            if (n == 1) {
                ans[0] = '8';
            }
            else if (n == 2) {
                ans = "88";
            }
            else if (k == 4) {
                ans[0] = '8';
                ans[n - 1] = '8';
                ans[n - 2] = '8';
                ans[1] = '8';
            }
            else if (k == 8) {
                ans[0] = '8';
                ans[n - 1] = '8';
                ans[n - 2] = '8';
                ans[1] = '8';
                ans[2] = '8';
                ans[n - 3] = '8';
            }

            return ans;
        }

        if (k == 6) {
            if (n == 1) {
                ans[0] = '6';
            }
            else if (n == 2) {
                ans = "66";
            }
            else {
                ans[0] = '8';
                ans[n - 1] = '8';

                if (n & 1) {
                    ans[n / 2]--;
                }
                else {
                    ans[n / 2] -= 2;
                    ans[(n - 1)/2] -= 2;
                }
            }

            return ans;
        }

        // k == 7

        while(true) {
            int rem = 0;

            for (int i = 0; i < n; i++) {
                rem *= 10;
                rem += (ans[i] - '0');
                rem %= 7;
            }

            if (rem == 0) {
                break;
            }

            if (n & 1) {
                ans[n / 2]--;
            }
            else {
                ans[n / 2]--;
                ans[(n - 1)/2]--;
            }
        }

        return ans;
    }
};
