import java.math.BigInteger;

class Solution {
    String largestPalindrome(int n, int k) {
        if (k == 1) {
            return "9".repeat(n);
        } else if (k == 2) {
            if (n == 1) return "8";
            else return "8" + "9".repeat(n - 2) + "8";
        } else if (k == 3) {
            return "9".repeat(n);
        } else if (k == 4) {
            if (n < 4) return "8".repeat(n);
            else return "88" + "9".repeat(n - 4) + "88";
        } else if (k == 5) {
            if (n == 1) return "5";
            else return "5" + "9".repeat(n - 2) + "5";
        } else if (k == 6) {
            if (n <= 2) return "6".repeat(n);

            if (n % 2 == 1) {
                return "8" + "9".repeat((n - 3) / 2) + "8" + "9".repeat((n - 3) / 2) + "8";
            } else {
                return "8" + "9".repeat((n - 4) / 2) + "77" + "9".repeat((n - 4) / 2) + "8";
            }

        } else if (k == 7) {
            if (n <= 2) return "7".repeat(n);
            if (n % 2 == 1) {
                for (int i = 9; i >= 0; i--) {
                    String str = "9".repeat((n - 1) / 2) + String.valueOf(i) + "9".repeat((n - 1) / 2);
                    BigInteger bigInt = new BigInteger(str);
                    BigInteger seven = new BigInteger("7");
                    BigInteger remainder = bigInt.remainder(seven);
                    if (remainder.compareTo(BigInteger.ZERO) == 0) return str;
                }
            } else {
                for (int i = 9; i >= 0; i--) {
                    String str = "9".repeat((n - 2) / 2) + String.valueOf(i).repeat(2) + "9".repeat((n - 2) / 2);
                    BigInteger bigInt = new BigInteger(str);
                    BigInteger seven = new BigInteger("7");
                    BigInteger remainder = bigInt.remainder(seven);
                    if (remainder.compareTo(BigInteger.ZERO) == 0) return str;
                }
            }
            return "";
        } else if (k == 8) {
            if (n < 6) return "8".repeat(n);
            else return "888" + "9".repeat(n - 6) + "888";
        } else {
            return "9".repeat(n);
        }
    }
}
