#include <string>
#include <cmath>
#include <algorithm>
using namespace std;

class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        }
        else if (k == 2) {
            if (n == 1) return "8";
            return "8" + string(n - 2, '9') + "8";
        }
        else if (k == 4) {
            if (n < 5) return string(n, '8');
            return "88" + string(n - 4, '9') + "88";
        }
        else if (k == 8) {
            if (n < 6) return string(n, '8');
            return "888" + string(n - 6, '9') + "888";
        }
        else if (k == 5) {
            if (n == 1) return "5";
            return "5" + string(n - 2, '9') + "5";
        }
        else if (k == 6) {
            if (n == 1) return "6";
            if (n == 2) return "66";
            if (n & 1) {
                return "8" + string((n - 3)/2, '9') + "8" + string((n - 3)/2, '9') + "8";
            }
            else {
                return "8" + string((n - 4)/2, '9') + "77" + string((n - 4)/2, '9') + "8";
            }
        }
        else if (k == 7) {
string leftHalf = string((n + 1) / 2, '9'); // 左半部分 '999...9'

        while (true) {
            string palindrome = leftHalf + string(leftHalf.rbegin() + (n % 2), leftHalf.rend());
            // cout << palindrome << '\n';

            // 计算 palindrome % 7 的结果
            int mod = 0;
            for (char c : palindrome) {
                mod = (mod * 10 + (c - '0')) % 7;
            }
            // cout << mod << '\n';

            // 如果找到满足条件的回文数，返回结果
            if (mod == 0) {
                return palindrome;
            }

            // 否则，减少左半部分的数值
            int i = leftHalf.length() - 1;
            while (i >= 0 && leftHalf[i] == '0') {
                leftHalf[i] = '9';
                i--;
            }
            if (i >= 0) {
                leftHalf[i]--;
            } else {
                // 如果整个左半部分都变成了 0，说明我们无法再找到更小的回文数
                return "";
            }
        }
        }
        return "";
    }
};
