class Solution(object):
    def largestPalindrome(self, n, k):
        """
        :type n: int
        :type k: int
        :rtype: str
        """
        def judge7(s):
            rest = 0
            for c in s:
                rest = rest * 10 + int(c) 
                rest %= 7

            return rest==0



        if k==1 or k==3 or k==9:
            return "9"*n
        if k==2:
            if n==1:  return "8"
            return "8" + (n-2)*"9" + "8"

        if k==5:
            if n==1:  return "5"
            return "5" + (n-2)*"9" + "5"

        if k==4:
            if n<=4:  return "8"*n
            return "88" + (n-4) * "9" + "88"
        if k==8:
            if n<=6:  return "8"*n
            return "888" + (n-6) * "9" + "888"     

        if k==6:
            if n==1:  return "6"
            if n==2:  return "66"
            if n % 2 == 0:   return "8" + "9" * ((n-4)//2) + "77" + "9" * ((n-4)//2) + "8"
            else:  return "8" + "9"*((n-3)//2) + "8" + "9"*((n-3)//2) + "8"

        if k==7:
            if n <= 2:  return "7"*n

            pick = 2 - n % 2


            for c in range(9,-1,-1):
                s = "9"*((n-pick)//2) + str(c)*(2-n%2) + "9"*((n-pick)//2)
                if judge7(s):
                    return s
