class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        if k == 2:
            if n == 1:
                return "8"
            return "8" + "9" * (n - 2) + "8"
        if k == 4:
            if n <= 4:
                return "8" * n
            return "88" + "9" * (n - 4) + "88"
        if k == 5:
            if n == 1:
                return "5"
            return "5" + "9" * (n - 2) + "5"
        if k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                return "66"
            elif n % 2 == 1:
                return "8" + "9" * ((n - 3) // 2) + "8" + "9" * ((n - 3) // 2) + "8"
            else:
                return "8"+"9"*((n - 4) // 2)+"77"+"9"*((n - 4) // 2)+"8"
        if k == 7:
            maps = [9, 7, 7, 5, 7, 7, 9, 4, 4, 6, 4, 4]
            mid = maps[n % 12]
            if n % 2 == 1:
                pstr = "9" * ((n - 1) // 2) + str(mid) + "9" * ((n - 1) // 2)
            else:
                pstr = "9" * ((n - 2) // 2) + str(mid) + str(mid) + "9" * ((n - 2) // 2)
            return pstr
        if k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "888" + "9" * (n - 6) + "888"
