char result[100009];

char* largestPalindrome(int n, int k) {
    if (k == 1 || k == 3 || k == 9) {
        for (int i = 0; i < n; i++) {
            result[i] = '9';
        }
    } else if (k == 2 || k == 5) {
        for (int i = 0; i < n; i++) {
            if (i > 0 && i < n - 1) {
                result[i] = '9';
            } else {
                result[i] = k == 2 ? '8' : '5';
            }
        }
    } else if (k == 4) {
        for (int i = 0; i < n; i++) {
            if (i > 1 && i < n - 2) {
                result[i] = '9';
            } else {
                result[i] = '8';
            }
        }
    } else if (k == 6) {
        int cur = 0;
        switch (n) {
            case 1:
                result[0] = '6';
                break;
            case 2:
                result[0] = '6';
                result[1] = '6';
                break;
            default:
                result[cur++] = '8';
                for (int i = 0; i < (n - 3) / 2; i++) {
                    result[cur++] = '9';
                }
                result[cur++] = n % 2 == 1 ? '8' : '7';
                if (n % 2 == 0) {
                    result[cur++] = '7';
                }
                for (int i = 0; i < (n - 3) / 2; i++) {
                    result[cur++] = '9';
                }
                result[n - 1] = '8';
                break;
        }
    } else if (k == 7) {
        int cur = 0;
        int mod1 = 0, mod2 = 0, mod3 = 1;
        switch (n) {
            case 1:
                result[0] = '7';
                break;
            case 2:
                result[0] = '7';
                result[1] = '7';
                break;
            default:
                for (int i = 0; i < (n - 1) / 2; i++) {
                    result[cur++] = '9';
                    mod1 = (mod1 * 10 + 9) % 7;
                }
                result[cur++] = '0';
                mod1 = (mod1 * 10) % 7;
                if (n % 2 == 0) {
                    result[cur++] = '0';
                    mod1 = (mod1 * 10) % 7;
                }
                for (int i = 0; i < (n - 1) / 2; i++) {
                    result[cur++] = '9';
                    mod1 = (mod1 * 10) % 7;
                    mod2 = (mod2 * 10 + 9) % 7;
                    mod3 = (mod3 * 10) % 7;
                }
                for (int i = 9; i >= 0; i--) {
                    int mod = 0;
                    if (n & 1) {
                        mod = (mod1 + mod2 + mod3 * i) % 7;
                    } else {
                        mod = (mod1 + mod2 + mod3 * i * 11) % 7;
                    }
                    if (mod == 0) {
                        if (n & 1) {
                            result[n / 2] = '0' + i;
                        } else {
                            result[n / 2 - 1] = '0' + i;
                            result[n / 2] = '0' + i;
                        }
                        break;
                    }
                }
                break;
        }
    } else if (k == 8) {
        for (int i = 0; i < n; i++) {
            if (i > 2 && i < n - 3) {
                result[i] = '9';
            } else {
                result[i] = '8';
            }
        }
    }
    result[n] = '\0';
    return result;
}
