class Solution {
public:
    string largestPalindrome(int n, int k) {
        // for (int i=1; i<=9; i++) {
        //     for (int j=1; j<=9; j++) {
        //         cout << largestPalindrome2(j, i) << " ";
        //     }
        //     cout << endl;
        // }
        return largestPalindrome2(n, k);
    }
    string largestPalindrome2(int n, int k) {
        string s(n, '9');
        if (k == 1 || k == 3 || k == 9) return s;
        if (k == 2) { s[0] = '8'; s[n-1] = '8'; return s; }
        if (k == 4) {
            s[0] = '8', s[n-1] = '8';
            if (n>=2) s[1] = '8', s[n-2] = '8';
            return s;
        }
        if (k == 5) { s[0] = '5'; s[n-1] = '5'; return s; }
        if (k == 6) {
            // 6
            // 66
            // 888
            // 8778
            // 89898
            // 897798
            // 8998998
            // 899779988
            // 8999899988
            if (n==1) return "6";
            if (n==2) return "66";
            if (n==3) return "888";
            if (n%2 == 1) {
                s[0] = '8', s[n-1] = '8', s[n/2] = '8';
                return s;
            } else {
                s[0] = '8', s[n-1] = '8', s[n/2-1] = '7', s[n/2] = '7';
                return s;
            }
        }
        if (k == 7) {
            for (char m = '9'; m >= '0'; m--) {
                s[n/2] = m; if (n%2==0) s[n/2-1] = m;
                if (isDivisible(s, k)) return s;
            }
        }
        if (k == 8) {
            if (n <= 6) return string(n, '8');
            s[0] = '8',   s[1] = '8', s[2] = '8';
            s[n-1] = '8', s[n-2] = '8', s[n-3] = '8';
            return s;
         }
        return "";
    }

    bool isDivisible(const string& s, int k) {
        int left = 0;
        for (int i=0; i<s.length(); i++) {
            left += s[i] - '0';
            left = left % k;
            left *= 10;
        }
        return left == 0;
    }
};
