class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) return string(n, '9');
        else if (k == 2) {
            if (n <= 2) return string(n, '8');

            string to_return(n, '9');
            to_return[0] = '8';
            to_return[n - 1] = '8';
            return to_return;
        } else if (k == 4) {
            if (n <= 4) return string(n, '8');

            string to_return(n, '9');
            to_return[0] = '8';
            to_return[1] = '8';
            to_return[n - 2] = '8';
            to_return[n - 1] = '8';
            return to_return;

        } else if (k == 8) {
            if (n <= 6) return string(n, '8');

            string to_return(n, '9');
            to_return[0] = '8';
            to_return[1] = '8';
            to_return[2] = '8';
            to_return[n - 3] = '8';
            to_return[n - 2] = '8';
            to_return[n - 1] = '8';
            return to_return;
        } else if (k == 5) {
            if (n <= 2) return string(n, '5');

            string to_return(n, '9');
            to_return[0] = '5';
            to_return[n - 1] = '5';
            return to_return;
        } else if (k == 6) {
            if (n <= 2) return string(n, '6');
            string to_return(n, '9');
            to_return[0] = '8';
            to_return[n - 1] = '8';

            if (n & 1) {
                to_return[n / 2] = '8';
            } else {
                to_return[n / 2] = '7';
                to_return[n / 2 - 1] = '7';
            }

            return to_return;


        } else {
            if (n <= 2) return string(n, '7');
            if (n % 6 == 0) return string(n, '9');

            int magic_number;
            if (n % 12 == 3) magic_number = 5;
            else if (n % 12 == 9) magic_number = 6;
            else if (n % 12 < 6) magic_number = 7;
            else magic_number = 4;

            string to_return(n, '9');
            if (n & 1) {
                to_return[n / 2] = magic_number + '0';
            } else {
                to_return[n / 2 - 1] = magic_number + '0';
                to_return[n / 2] = magic_number + '0';
            }

            return to_return;
        }
    }
};
