class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(100000)
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        if k == 2:
            if n == 1:
                return '8'
            return '8' + '9' * (n - 2) + '8'
        if k == 4:
            if n <= 4:
                return '8' * n
            return '88' + '9' * (n - 4) + '88'
        if k == 8:
            if n <= 6:
                return '8' * n
            return '888' + '9' * (n - 6) + '888'
        if k == 5:
            if n == 1:
                return '5'
            return '5' + '9' * (n - 2) + '5'
        if k == 7:
            if n <= 2:
                return '7' * n
            mid = 1 if n % 2 == 1 else 2
            for i in range(9, -1, -1):
                x = int('9' * ((n - mid) // 2) + str(i) * mid + '9' * ((n - mid) // 2))
                if x % 7 == 0:
                    return str(x)
        if k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n % 2 == 1:
                return '8' + '9' * ((n - 3) // 2) + '8' + '9' * ((n - 3) // 2) + '8'
            return '8' + '9' * ((n - 4) // 2) + '77' + '9' * ((n - 4) // 2) + '8'
