def solve(n, k):
    if k in [1, 3, 9]:
        return "9" * n
    # k 的倍数的最后一位
    d = [[] for _ in range(10)]
    for k_ in range(1, 10):
        for x in range(100):
            t = k_ * x % 10
            d[k_].append(t)
    d = [sorted(list(set(di))) for di in d]
    if n <= 2 and k not in [6, 7, 8]:
        return str(d[k][-1]) * n
    if k == 2:
        return "8" + "9" * (n - 2) + "8"
    if k == 5:
        return "5" + "9" * (n - 2) + "5"
    if k == 4:
        if n <= 4:
            return "8" * n
        return "88" + "9" * (n - 4) + "88"
    if n <= 2:
        # 6 7 8 的情况
        for x in [88, 77, 66, 8, 7, 6]:
            if len(str(x)) == n and x % k == 0:
                return str(x)

    p = [1]  # pow(10, i, k)
    for i in range(1, n):
        p.append(p[-1] * 10 % k)
    # mod =
    res = [0] * n
    left = 0
    i = 0
    j = n - 1
    while i + 1 < j:
        if i == 0:
            # 6 8 以 8 结尾, 7 以 9 结尾
            x = 8 if k != 7 else 9
        elif i <= 2 and k == 8:
            x = 8
        else:
            x = 9
        left += p[i] * x + p[j] * x
        left %= k
        res[i] = res[j] = x
        i += 1
        j -= 1
    for x in reversed(range(10)):
        res[i] = res[j] = x
        left += p[i] * x + p[j] * x * (i != j)
        left %= k
        if left == 0:
            break
        left -= p[i] * x + p[j] * x * (i != j)
        left %= k
    return "".join(map(str, res))

    # print(d)


class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        return solve(n, k)
