class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def divisible(a: str, b: int):
            carry = 0
            for i in a:
                carry = (carry * 10 + int(i)) % b
            return carry == 0

        if k == 1:
            return '9' * n
        elif k == 2:
            if n == 1:
                return '8'
            return '8' + '9' * (n - 2) + '8'
        elif k == 3:
            return '9' * n
        elif k == 4:
            if n <= 4:
                return '8' * n
            else:
                return '88' + '9' * (n - 4) + '88'
        elif k == 5:
            if n == 1:
                return '5'
            else:
                return '5' + '9' * (n - 2) + '5'
        elif k == 6:
            if n == 1:
                return '6'
            elif n == 2:
                return '66'
            elif n % 2 == 1:
                num_9 = (n - 3) // 2
                return '8' + '9' * num_9 + '8' + '9' * num_9 + '8'
            else:
                num_9 = (n - 4) // 2
                return '8' + '9' * num_9 + '77' + '9' * num_9 + '8'
        elif k == 7:
            if n == 1:
                return '7'
            elif n == 2:
                return '77'
            else:
                num_9 = (n - 1) // 2
                # odd: 999_999, even: 9999__9999
                for choice in range(9, -1, -1):
                    result = '9' * num_9 + str(choice) * (n - num_9 * 2) + '9' * num_9
                    if (divisible(result, 7)):
                        return result
        elif k == 8:
            if n <= 6:
                return '8' * n
            else:
                return '888' + '9' * (n - 6) + '888'
        elif k == 9:
            return '9' * n

        return 0
