class Solution {
public:
    bool isDivisible(string s, int k) {
        int carry = 0;
        for(int i=0;i<s.size();i++) {
            int v = s[i]-'0';
            v = (carry*10) + v;
            carry = v % k;
        }
        if(carry == 0) {
            return true;
        }

        return false;
    }
    string largestPalindrome(int n, int k) {
        string ret = "";
        if(k==1 || k==3 || k==9) {
            for(int i=0;i<n;i++) {
                ret += '9';
            }
            return ret;
        }

        if(k==2) {
            if(n==1) {
                return "8";
            }
            if(n==2) {
                return "88";
            }
            ret += '8';
            for(int i=1;i<n-1;i++) {
                ret += '9';
            }
            ret += '8';
            return ret;
        }

        // if(k==4 || k==8) {
        //     for(int i=0;i<n;i++) {
        //         ret += '8';
        //     }
        //     return ret;
        // }

        if(k==4) {
            if(n<=4) {
                for(int i=0;i<n;i++) {
                    ret += '8';
                }
                return ret;
            }


            string s = "88";
            for(int i=2;i<n-2;i++) {
                ret += "9";
            }

            return s+ret+s;

        }

        if(k==8) {
            if(n<=6) {
                for(int i=0;i<n;i++) {
                    ret += '8';
                }
                return ret;
            }

            string s = "888";
            for(int i=3;i<n-3;i++) {
                ret += "9";
            }
            return s+ret+s;
        }

        if(k==5) {
            if(n==1) {
                return "5";                
            }
            if(n==2) {
                return "55";
            }
            ret += '5';
            for(int i=1;i<n-1;i++) {
                ret += '9';
            }
            ret += '5';
            return ret;
        }



        if(k==6) {
            if(n==1) {
                return "6";
            }
            if(n == 2) {
                return "66";
            }

            if(n%2 == 1) {
                // odd; fill up n/2 chars with 8 and last n/2 chars with 8;
                string s = "";
                for(int i=0;i<((n/2)-1);i++) {
                    s += '9';
                }

                for(int i=9;i>=0;i--) {
                    char c = '0'+i;
                    string chk = '8'+s+c+s+'8';
                    if(isDivisible(chk, k)) {
                        return chk;
                    }
                }                
            } else {
                // even; fill up n/2 -1 chars with 8;
                string s = "";
                for(int i=1;i< ((n/2)-1); i++) {
                    s += '9';
                }


                for(int i=9;i>=0;i--) {
                    char c = '0' + i;
                    string chk = '8' + s + c + c + s + '8';
                    if(isDivisible(chk, k)) {
                        return chk;
                    }
                }                
            }          
        }

        if(k==7) {
            if(n==1) {
                return "7";
            }
            if(n==2) {
                return "77";
            }

            if(n%2 == 1) {

                // odd; fill up n/2 chars with 8 and last n/2 chars with 8;
                string s = "";
                for(int i=0;i<(n/2);i++) {
                    s += '9';
                }

                for(int i=9;i>=0;i--) {
                    char c = '0'+i;
                    string chk = s+c+s;

                    if(isDivisible(chk, k)) {
                        return chk;
                    }
                }                
            } else {
                // even; fill up n/2 -1 chars with 8;
                string s = "";
                for(int i=0;i< ((n/2)-1); i++) {
                    s += '9';
                }

                for(int i=9;i>=0;i--) {
                    char c = '0' + i;
                    string chk = s + c + c + s;
                    if(isDivisible(chk, k)) {
                        return chk;
                    }
                }                
            }



        }
        return "0";

    }
};
