string seven(int n) {
    if (n < 3) {
        return string(n, '7');
    }
    string str(n, '9');
    string num = nums[(n - 3) % 12];
    int idx = (n - 1) / 2;
    for (char ch: num) {
        str[idx++] = ch;
    }
    return str;
}

string six(int n) {
    if (n == 1)
        return "6";
    if (n == 2)
        return "66";
    string str(n, '9');
    str[0] = '8';
    str.back() = '8';
    if (n & 1)
        str[n / 2] = '8';
    else {
        str[(n - 1) / 2] = '7';
        str[(n - 1) / 2 + 1] = '7';
    }
    return str;
}

class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 or k == 3 or k == 9)
            return one(n);
        if (k == 2)
            return two(n);
        if (k == 4)
            return four(n);
        if (k == 5)
            return five(n);
        if (k == 6)
            return six(n);
        if (k == 7)
            return seven(n);
        return eight(n);
    }
};
