class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        elif k == 2:
            if n <= 2:
                return "8" * n
            else:
                return "8" + "9" * (n-2) + "8"
        elif k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "88" + "9" * (n-4) + "88"
        elif k == 5:
            if n <= 2:
                return "5" * n
            else:
                return "5" + "9" * (n-2) + "5"
        elif k == 6:
            if n <= 2:
                return "6" * n
            elif n % 2 == 1:
                return "8" + "9" * ((n-3) // 2) + "8" + "9" * ((n-3) // 2) + "8"
            else:
                return "8" + "9" * ((n-4) // 2) + "77" + "9" * ((n-4) // 2) + "8"
        elif k == 7:
            if n <= 2:
                return "7" * n
            elif n % 2 == 1:
                remainders_power_10 = [1]
                for i in range(1, n):
                    remainders_power_10.append((remainders_power_10[-1] * 10) % 7)

                skip_idx = (n-1) // 2
                nines_remainder = (sum([
                    remainders_power_10[i]
                    for i in range(n)
                    if i != skip_idx
                ]) * 9) % 7
                a = None
                for a in range(9, -1, -1):
                    if (nines_remainder + remainders_power_10[skip_idx] * a) % 7 == 0:
                        break
                return "9" * ((n-1) // 2) + str(a) + "9" * ((n-1) // 2)
            else:
                remainders_power_10 = [1]
                for i in range(1, n):
                    remainders_power_10.append((remainders_power_10[-1] * 10) % 7)
                skip_indices = {n // 2, n // 2 - 1}
                nines_remainder = (sum([
                    remainders_power_10[i]
                    for i in range(n)
                    if i not in skip_indices
                ]) * 9) % 7

                a = None
                remainders_middle = sum([
                    remainders_power_10[idx]
                    for idx in skip_indices
                ])
                for a in range(9, -1, -1):
                    if (nines_remainder + remainders_middle * a) % 7 == 0:
                        break
                return "9" * (n // 2 - 1) + str(a) + str(a) + "9" * (n // 2 - 1)
        elif k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "888" + "9" * (n-6) + "888"
