class Solution {
public:
    string largestPalindrome(int n, int k) {
        string result;
        if (k == 2) {
            if (n == 1) {
                result = "8";
            } else {
                result = "8" + string(n - 2, '9') + "8";
            }
        } else if (k == 1 || k == 3 || k == 9) {
            result = string(n, '9');
        } else if (k == 4) {
            if (n == 1) {
                result = "8";
            } else if (n == 2) {
                result = "88";
            } else if (n == 3) {
                result = "888";
            } else {
                result = "88" + string(n - 4, '9') + "88";
            }
        } else if (k == 5) {
            if (n == 1) {
                result = "5";
            } else {
                result = "5" + string(n - 2, '9') + "5";
            }
        } else if (k == 6) {
            if (n == 1) {
                result = "6";
            } else if (n == 2) {
                result = "66";
            } else if (n % 2) {
                result = "8" + string(n / 2 - 1, '9') + "8" + string(n / 2 - 1, '9') + "8";
            } else {
                result = "8" + string(n / 2 - 2, '9') + "77" + string(n / 2 - 2, '9') + "8";
            }
        } else if (k == 8) {
            if (n <= 5) {
                result = string(n, '8');
            } else {
                result = "888" + string(n - 6, '9') + "888";
            }
        } else {
            if (n <= 2) {
                result = string(n, '7');
            } else {
                int deg = 1;
                for (int i = 0; i < (n - 1) / 2; ++i) {
                    deg = deg * 10 % 7;
                }

                int mod = 0;
                for (int i = 0; i < n; ++i) {
                    mod *= 10;
                    mod = (mod + 9) % 7;
                }
                if (n % 2) {
                    int diff;
                    for (diff = 0; diff < 9; ++diff) {
                        if (diff * deg % 7 == mod) {
                            break;
                        }
                    }
                    char c = '9' - diff;
                    result = string(n / 2, '9') + c + string(n / 2, '9');
                } else {
                    int diff;
                    for (diff = 0; diff < 9; ++diff) {
                        if ((diff * 10 + diff) * deg % 7 == mod) {
                            break;
                        }
                    }
                    char c = '9' - diff;
                    result = string(n / 2 - 1, '9') + string(2, c) + string(n / 2 - 1, '9');
                }
            }
        }
        return result;
    }
};
