class Solution {
public:
    bool mod(string s,int md, bool odd){
        string t = s; 
        if(odd){
            t.pop_back();
        }
        reverse(t.begin(),t.end()); 
        s+=t;
        int x = 0;
        for(int i=0;i<s.size();++i){
            x=(x*10+(s[i]-'0'))%7;
        } 
        return x==0;
    }
    bool solve(int idx,int n, string &s, bool odd){
        if(idx>=n){
            if(mod(s,7, odd)){
                return true;
            } 
            return false;
        }  

        for(char ch='9';ch>='0';--ch){
            if(idx==0 && ch=='0')continue; 
            s+=ch;
            if(solve(idx+1,n,s, odd)){
                return true;
            } 
            s.pop_back();
        } 
        return false;
    }
