class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans;
        string ans_seven[12] = {"7", "77","5", "77", "7", "", "4", "44", "6", "44", "4", ""};
        if ((k == 1) || (k == 3) || (k == 9)) {
            for (int i = 0; i < n; ++ i) ans += "9";
        }
        if (k == 2) {
            ans += "8";
            for (int i = 0; i < n - 2; ++i) ans += "9";
            if (n >= 2) ans += "8";
        }
        if (k == 5) {
            ans += "5";
            for (int i = 0; i < n - 2; ++i) {
                ans += "9";
            }
            if (n >= 2) ans += "5";
        }
        if (k == 4) {
            if (n <= 4) {
                for (int i = 0; i < n; ++i) ans += "8";
            } else {
                ans += "88";
                for (int i = 0; i < n - 4; ++i) ans += "9";
                ans += "88";
            }
        }
        if (k == 8) {
            if (n <= 6) {
                for (int i = 0; i < n; ++i) ans += "8";
            } else {
                ans += "888";
                for (int i = 0; i < n - 6; ++i) ans += "9";
                ans += "888";
            }
        }
        if (k == 6) {
            if (n == 1) {
                ans = "6";
            } else if (n == 2) {
                ans = "66";
            } else if (n % 2 == 1) {
                ans += "8";
                for (int i = 0; i < n / 2 - 1; ++i) ans += "9";
                ans += "8";
                for (int i = 0; i < n / 2 - 1; ++i) ans += "9";
                ans += "8";
            } else {
                ans += "8";
                for (int i = 0; i < n / 2 - 2; ++i) ans += "9";
                ans += "77";
                for (int i = 0; i < n / 2 - 2; ++i) ans += "9";
                ans += "8";
            }
        }
        if (k == 7) {
            int index = (n - 1) % 12;
            int len = n - ans_seven[index].length();
            for (int i = 0; i < len / 2; ++i) ans += "9";
            ans += ans_seven[index];
            for (int i = 0; i < len / 2; ++i) ans += "9";
        }
        return ans;
    }
};
