class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9: return "9" * n
        if k == 5:
            if n == 1: return "5"
            return "5" + "9" * (n-2) + "5"
        if k == 2:
            if n == 1: return "8"
            return "8" + "9" * (n-2) + "8"
        if k == 4:
            if n <= 4: return "8" * n
            return "88" + "9" * (n-4) + "88"
        if k == 8:
            if n <= 6: return "8" * n
            return "888" + "9" * (n-6) + "888"
        if k == 6:
            if n <= 2: return "6" * n
            if n % 2: return "8" + "9"*((n-3)//2) + "8" + "9"*((n-3)//2) + "8"
            return "8" + "9"*((n-4)//2) + "77" + "9"*((n-3)//2) + "8"
        if k == 7:
            if n <= 2: return "7" * n
            modseven = [None] * (n+1)
            modseven[1] = 1
            for i in range(2, len(modseven)):
                modseven[i] = (10*modseven[i-1])%7
            # <= modseven[i]
            modsevensum = [0] * (n+1)
            for i in range(1, len(modsevensum)):
                modsevensum[i] = modsevensum[i-1] + modseven[i]
            if n % 2:
                current = (modsevensum[n]-modsevensum[n-(n//2)]) + modsevensum[n//2]
                current = (current*9) % 7
                for attempt in reversed(range(10)):
                    if (current + attempt*(modseven[n//2+1])) % 7 == 0:
                        return "9" * (n//2) + str(attempt) + "9" * (n//2)
            else:
                current = (modsevensum[n]-modsevensum[n-(n//2)+1]) + modsevensum[n//2-1]
                current = (current*9) % 7
                for attempt in reversed(range(10)):
                    if (current + attempt*(modseven[n//2+1]+modseven[n//2])) % 7 == 0:
                        return "9" * (n//2-1) + str(attempt) * 2 + "9" * (n//2-1)
