class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        if k == 2:
            return "8" if n == 1 else "8" + "9" * (n - 2) + "8"
        if k == 4:
            return "8" * n if n < 4 else "88" + "9" * (n - 4) + "88"
        if k == 8:
            return "8" * n if n < 6 else "888" + "9" * (n - 6) + "888"
        if k == 5:
            return "5" if n == 1 else "5" + "9" * (n - 2) + "5"
        if k == 6:
            if n < 3:
                return "6" * n
            if n % 2 == 1:
                # 89X98 => X = 8
                half = "8" + "9" * (n // 2 - 1)
                return half + "8" + half[::-1]
            else:
                # 89977998 => X = 7
                half = "8" + "9" * (n // 2 - 2) + "7"
                return half + half[::-1]
        if k == 7:
            def mod7(mid: int) -> int:
                mod = 0
                for i in range(n):
                    mod *= 10
                    mod += 9 if i != n // 2 else mid
                    mod %= 7
                return mod

            def mod7_2(mid: int) -> int:
                mod = 0
                for i in range(n):
                    mod *= 10
                    mod += mid if (i == n // 2 or i == n // 2 - 1) else 9
                    mod %= 7
                return mod

            if n < 3:
                return "7" * n
            if n % 2 == 1:
                # 9X9
                for m in range(9, -1, -1):
                    if mod7(m) == 0:
                        half = "9" * (n // 2)
                        return half + str(m) + half[::-1]
            else:
                # 9XX9
                for m in range(9, -1, -1):
                    if mod7_2(m) == 0:
                        half = "9" * (n // 2 - 1) + str(m)
                        return half + half[::-1]
