class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        elif k == 2:
            if n >= 2:
                return '8' + '9' * (n - 2) + '8'
            else:
                return '8' * n
        elif k == 4:
            if n <= 4:
                return '8' * n
            else:
                return '88' + '9' * (n - 4) + '88'
        elif k == 8:
            if n <= 6:
                return '8' * n
            else:
                return '888' + '9' * (n - 6) + '888'
        elif k == 5:
            if n >= 2:
                return '5' + '9' * (n - 2) + '5'
            else:
                return '5' * n
        elif k == 6:
            if n % 2 == 0:
                if n >= 4:
                    return '8' + '9' * int((n - 4) / 2) + '77' + '9' * int((n - 4) / 2) + '8'
                else:
                    return '6' * n
            else:
                if n >= 3:
                    return '8' + '9' * int((n - 3) / 2) + '8' + '9' * int((n - 3) / 2) + '8'
                else:
                    return '6'
        elif k == 7:
            rs9 = [0, 2, 1, 5, 3, 4]
            rs11 = [4, 5, 1, 3, 2, 6]
            rs0 = [1, 3, 2, 6, 4, 5]
            tr = rs9[n % 6]
            if n % 2 == 0:
                for i in range(0, 10):
                    if (tr - rs11[int((n - 2) / 2) % 6] * i) % 7 == 0:
                        return '9' * int((n - 2) / 2) + chr(ord('0') + 9 - i) * 2 + '9' * int((n - 2) / 2)
            else:
                if n == 1:
                    return '7'
                else:
                    for i in range(0, 10):
                        if (tr - rs0[int((n - 1) / 2) % 6] * i) % 7 == 0:
                            return '9' * int((n - 1) / 2) + chr(ord('0') + 9 - i) + '9' * int((n - 1) / 2)

        return '0'
