impl Solution {
    pub fn largest_palindrome(n: i32, k: i32) -> String {
        match k {
            1 | 3 | 9 => "9".repeat(n as usize),
            2 => if n <= 2 { "8".repeat(n as usize) } else { format!("8{}8", "9".repeat((n - 2).max(0) as usize)) },
            4 => if n <= 4 { "8".repeat(n as usize) } else { format!("88{}88", "9".repeat((n - 4).max(0) as usize)) },
            5 => if n <= 2 { "5".repeat(n as usize) } else { format!("5{}5", "9".repeat((n - 2).max(0) as usize)) },
            6 => {
                if n <= 2 {
                    "6".repeat(n as usize)
                } else if n % 2 == 1 {
                    let x = "9".repeat(((n - 3) / 2) as usize);
                    format!("8{}8{}8", &x, &x)
                } else {
                    let x = "9".repeat(((n - 4) / 2) as usize);
                    format!("8{}77{}8", &x, &x)
                }
            }
            7 => {
                if n <= 2 {
                    "7".repeat(n as usize)
                } else {
                    let cycle = (n - 3) % 12;
                    let x = "9".repeat(((n-1)/2) as usize);
                    match cycle {
                        0 => format!("{}5{}", &x, &x),
                        1 => format!("{}77{}", &x, &x),
                        2 => format!("{}7{}", &x, &x),
                        3 => format!("{}99{}", &x, &x),
                        4 => format!("{}4{}", &x, &x),
                        5 => format!("{}44{}", &x, &x),
                        6 => format!("{}6{}", &x, &x),
                        7 => format!("{}44{}", &x, &x),
                        8 => format!("{}4{}", &x, &x),
                        9 => format!("{}99{}", &x, &x),
                        10 => format!("{}7{}", &x, &x),
                        11 => format!("{}77{}", &x, &x),
                        _ => unreachable!(),
                    }
                }
            }
            8 => if n <= 6 { "8".repeat(n as usize) } else { format!("888{}888", "9".repeat(n as usize - 6)) },
            _ => unreachable!(),
        }
    }
}
