class Solution {
    public String largestPalindrome(int n, int k) {
        char[] array = new char[n];
        Arrays.fill(array,'9');
        if(k == 1 || k == 9 || k == 3){

        }
        else if(k == 5){
            array[0] = '5';
            array[n-1] = '5';
        }
        else if(k == 2){
            array[0] = '8';
            array[n-1] = '8';
        }
        else if(k == 4){
            array[0] = '8';
            array[n-1] = '8';
            if(n>2){
                array[1]='8';
                array[n-2] = '8';
            }
        }
        else if (k == 8){
            array[0] = '8';
            array[n-1] = '8';
            if(n>2){
                array[1]='8';
                array[n-2] = '8';
            }
            if(n>4){
                array[2]='8';
                array[n-3] = '8';
            }
        }
        else if (k == 6){
            if(n ==1){
                return "6";
            }else if(n == 2){
                return "66";
            }
            array[0] = '8';
            array[n-1] = '8';
            if(n%2 == 0){
                array[n/2 - 1] = '7';
                array[n/2] = '7';
            }else{
                array[n/2] = '8';
            }
        }
        else {
            if(n == 1){
                return "7";
            }
            if(n == 2){
                return "77";
            }
            int modSum = 0;
            int pow = 1;
            int midPow =1;
            for(int i =0; i < n; i++){
                modSum += 9*pow;
                modSum %= 7;
                if( i == n/2 - 1){
                    midPow = (int)pow;
                }
                pow *= 10;
                pow %= 7;

            }
            if(n%2 == 1){
                midPow *= 10;
                midPow %= 7;
                int tmp = modSum - midPow*9;
                tmp %= 7;
                if(tmp < 0){
                    tmp += 7;
                }
                for(int i =9; i >= 0; i--){
                    if ((tmp + (midPow)*i)%7 == 0){
                        array[n/2] = (char)((int)'0' + i);
                        break;
                    }
                }

            }
            else{
                midPow = midPow + (midPow*10);
                midPow %= 7;
                int tmp = modSum - midPow*9;
                tmp %= 7;
                if(tmp < 0){
                    tmp += 7;
                }
                for(int i =9; i >= 0; i--){
                    if ((tmp + (midPow)*i)%7 == 0){
                        array[n/2-1] =  (char)((int)'0' + i);
                        array[n/2] =  (char)((int)'0' + i);
                        break;
                    }
                }

            }


        }
        return String.valueOf(array);
    }
}
