import sys
sys.set_int_max_str_digits(100_000)

class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n == 1:
            return {
                1: '9',
                2: '8',
                3: '9',
                4: '8',
                5: '5',
                6: '6',
                7: '7',
                8: '8',
                9: '9',
            }[k]
        elif n == 2:
            return {
                1: '99',
                2: '88',
                3: '99',
                4: '88',
                5: '55',
                6: '66',
                7: '77',
                8: '88',
                9: '99',
            }[k]
        elif n == 3:
            return {
                1: '999',
                2: '898',
                3: '999',
                4: '888',
                5: '595',
                6: '888',
                7: '959',
                8: '888',
                9: '999',
            }[k]
        else:
            if k == 1:
                return '9' * n
            elif k == 2:
                return '8' + '9' * (n - 2) + '8'
            elif k == 3:
                return '9' * n
            elif k == 4:
                return '88' + '9' * (n - 4) + '88'
            elif k == 5:
                return '5' + '9' * (n - 2) + '5'
            elif k == 6:
                if n % 2 == 1:
                    m = (n - 3) // 2
                    return '8' + '9' * m + '8' + '9' * m + '8'
                else:
                    m = (n - 4) // 2
                    return '8' + '9' * m + '77' + '9' * m + '8'
            elif k == 7:
                if n % 2 == 1:
                    m = (n - 1) // 2
                    for i in range(9, -1, -1):
                        if (candidate := int('9' * m + str(i) + '9' * m)) % 7 == 0:
                            return str(candidate) # NOTE 1: Forgot to `str(...)`.
                else:
                    m = (n - 2) // 2
                    for i in range(9, -1, -1):
                        if (candidate := int('9' * m + str(i) * 2 + '9' * m)) % 7 == 0:
                            return str(candidate)
            elif k == 8:
                if n < 6:
                    return '8' * n
                else:
                    return '888' + '9' * (n - 6) + '888'
            elif k == 9:
                return '9' * n
            else:
                assert False
        assert False
