class Solution {
public:
    bool check_divide_7(string s){
        int size = s.size(),seglen;
        string segment;
        long long sum = 0;
        long long multiplier = 1;
        for(int i=size-1;i>=0;i-=3){
            seglen = min(3,i+1);
            if(seglen < 3){
                segment = s.substr(0,seglen);
            }else{
                segment = s.substr(i-2,3);
            }
            if(seglen == 3){
                sum += (((segment[0] - '0') * 100 + (segment[1] - '0') * 10 + (segment[2] - '0') * 1) * multiplier);
            }else if(seglen == 2){
                sum += (((segment[0] - '0') * 10 + (segment[1] - '0') * 1) * multiplier);
            }else{
                sum += (((segment[0] - '0') * 1) * multiplier);
            }
            multiplier *= -1;
        }
        if(sum % 7 == 0){
            return true;
        }return false;
    }
    string largestPalindrome(int n, int k) {
        if(k == 1 || k == 3 || k == 9){
             return string(n,'9');
        }
        if(k == 2){
            string s = string(n,'9');
            s[0] = '8';
            s[n-1] = '8';
            return s;
        }
        if(k == 4)
        {
            if(n == 1){
                return "8";
            }
            if(n == 2){
                return "88";
            }
            if(n == 3){
                return "888";
            }
            if(n == 4){
                return "8888";
            }
            string s = string(n,'9');
            s[0] = '8';
            s[1] = '8';
            s[n-2] = '8';
            s[n-1] = '8';
            return s;
        }
        if(k == 5)
        {
            string s = string(n,'9');
            s[0] = '5';
            s[n-1] = '5';
            return s;
        }
        if(k == 6)
        {
            if(n == 1){
                return "6";
            }
            if(n == 2){
                return "66";
            }
            if(n == 3){
                return "888";
            }
            string s = string(n,'9');
            s[0] = '8';
            s[n-1] = '8';
            int sum = 9 * n - 2;
            char c;
            if(n % 2 == 0){
                if(sum % 3 == 1){
                    c = '7';   
                }else if(sum % 3 == 2){
                    c = '8';
                }else{
                    c = '9';
                }
                s[n/2 - 1] = c;
                s[n/2] = c;
            }else{
                if(sum % 3 == 1){
                    c = '8';   
                }else if(sum % 3 == 2){
                    c = '7';
                }else{
                    c = '9';
                }
                s[n/2] = c;
            }
            return s;
        }
        if(k == 8)
        {
            if(n == 1){
                return "8";
            }
            if(n == 2){
                return "88";
            }
            if(n == 3){
                return "888";
            }
            if(n == 4){
                return "8888";
            }
            if(n == 5){
                return "88888";
            }
            string s = string(n,'9');
            s[0] = '8';
            s[1] = '8';
            s[2] = '8';
            s[n-3] = '8';
            s[n-2] = '8';
            s[n-1] = '8';
            return s;
        }
        if(k == 7)
        {
            if(n == 1){
                return "7";
            }
            if(n == 2){
                return "77";
            }
            if(n == 3){
                return "959";
            }
            if(n == 4){
                return "9779";
            }
            if(n == 5){
                return "99799";
            }
            if(n == 6){
                return "999999";
            }
            string s = string(n,'9');
            char c;
            if(n % 2 == 0){
                for(int i=9;i>=0;--i){
                    c = '0' + i;
                    s[n/2 - 1] = c;
                    s[n/2] = c;
                    if(check_divide_7(s)){
                        return s;
                    }
                }
            }else{
                for(int i=9;i>=0;--i){
                    c = '0' + i;
                    s[n/2] = c;
                    if(check_divide_7(s)){
                        return s;
                    }
                }
            }
            return s;
        }
        return "";
    }
};
