class Solution {
public:
    int mod[100002][10];
    string ans = "";
    int mx,nn,k;
    bool aer = false;
    int memo[100000][10];
    bool dp(int i,int num){
        if(aer == true)return true;
        if(memo[i][num] != -1)return memo[i][num];
        if(i == mx){
            for(int j = 9;j >= 0;j--){
                int m = nn % 2 == 0 ? (mod[i][j] + mod[nn-1-i][j]) % k : mod[i][j];
                if((m + num) % k == 0){
                    if(nn % 2){
                        ans[i] = '0' + j;
                    }else{
                        ans[i] = '0' + j;
                        ans[nn-1-i] = '0' + j;
                    }
                    aer = true;
                    return true;
                }
            }
            return false;
        }

        for(int j = 9;j >= (i == 0);j--){
            int m = (mod[i][j] + mod[nn-1-i][j]) % k;
            ans[i] = '0' + j;
            ans[nn-1-i] = '0' + j;
            int newnum = (num + m) % k;
            dp(i + 1,newnum);
            if (aer == true)return false;
        }
        memo[i][num] = false;
        return false; 
    }
    string largestPalindrome(int n, int k) {
        ans.resize(n);
        this->k = k;
        memset(memo,-1,sizeof(memo));
        for(int i = 0;i < n;i++){
            for(int j = 0;j <= 9;j++){
                if(i == 0){
                    mod[i][j] = j % k;
                }else{
                    mod[i][j] = (mod[i-1][j] * 10) % k;
                }
            }
        }
        nn = n;
        mx = (n + 1) / 2 - 1;
        dp(0,0);
        return ans;
    }
};
