class Solution {
public:
  string largestPalindrome(int n, int k) {
    if (n == 1) {
      for (int i = 9; i >= 0; --i)
        if (i % k == 0)
          return to_string(i);
      return to_string(k);
    }
    if (n == 2) {
      for (int i = 99; i >= 10; --i) {
        if (i % 10 == i / 10 % 10 && i % k == 0) {
          return to_string(i);
        }
      }
      return "";
    }
    if (n == 3) {
      for (int i = 999; i >= 100; --i) {
        if (i % 10 == i / 100 % 10 && i % k == 0) {
          return to_string(i);
        }
      }
      return "";
    }

    vector<int> f(n);
    f[0] = 1 % k;
    for (int i = 1; i < n; ++i)
      f[i] = f[i - 1] * 10 % k;

    int m = (n + 1) / 2;
    vector<int> g(m);
    for (int i = 0; i < m; ++i) {
      int j = n - 1 - i;
      if (i == j)
        g[i] = f[i];
      else
        g[i] = (f[i] + f[j]) % k;
    }
    reverse(g.begin(), g.end());

    vector<vector<bool>> dp(m, vector<bool>(k));
    for (int h = 0; h < k; ++h)
      dp[0][h * g[0] % k] = true;

    for (int i = 1; i < m; ++i) {
      for (int j = 0; j < k; ++j)
        if (dp[i - 1][j]) {
          for (int h = 0; h < 10; ++h) {
            if (i == m - 1 && h == 0) {
              continue;
            }
            dp[i][(j + h * g[i]) % k] = true;
          }
        }
    }

    vector<int> ret(m);
    for (int i = m - 1, s = 0; i >= 0; --i) {
      if (i == 0) {
        for (int h = 9; h >= 0; --h)
          if (h * g[i] % k == s) {
            ret[i] = h;
            break;
          }
      } else {

        for (int h = 9; h >= 0; --h)
          if (dp[i - 1][((s - (h * g[i] % k)) % k + k) % k]) {
            ret[i] = h;
            s = ((s - (h * g[i] % k)) % k + k) % k;
            break;
          }
      }
    }

    reverse(ret.begin(), ret.end());
    vector<int> res(n);
    for (int i = 0; i < m; ++i)
      res[i] = res[n - 1 - i] = ret[i];
    string ans;
    for (int val : res)
      ans += to_string(val);
    return ans;
  }
};
