class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k==1:
            return '9'*n
        if k==2:
            if n<2:
                return '8'
            return '8' + '9'*(n-2) + '8'
        if k==3:
            return '9'*n
        if k==4:
            if n<4:
                return '8'*n
            return '88' + '9'*(n-4) + '88'
        if k==5:
            if n<2:
                return '5'
            return '5' + '9'*(n-2) + '5'
        if k==6:
            if n<3:
                return['6', '66', '888'][n-1]
            if n%2 == 0:
                return '8' + '9'*((n-4)//2) + '77' + '9'*((n-4)//2) + '8'
            return '8' + '9'*((n-3)//2) + '8' + '9'*((n-3)//2) + '8'
        if k==7:
            if n<3:
                return '7'*n
            r = [1,3,2,6,4,5]
            if n%2 == 0:
                d = 0
                for i in range(n):
                    if i != n//2 and i != n//2-1:
                        d += 9 * r[i%6]
                for i in reversed(range(10)):
                    if (d + i * r[(n//2)%6] + i * r[(n//2-1)%6] ) % 7 == 0:
                        return '9' * ((n-2)//2) + str(i) * 2 + '9' * ((n-2)//2)
            else:
                d = 0
                for i in range(n):
                    if i != n//2:
                        d += 9 * r[i%6]
                for i in reversed(range(10)):
                    if (d + i * r[(n//2)%6] ) % 7 == 0:
                        return '9' * ((n-1)//2) + str(i) * 1 + '9' * ((n-1)//2)
        if k==8:
            if n<6:
                return '8'*n
            return '888' + '9'*(n-6) + '888'
        if k==9:
            return '9'*n
        return '9'*n
