class Solution {
public:
    bool is7divisible(vector<char> num)
    {
        long long remainder = 0;

        for (char digit: num) 
        {
            int currentDigit = digit - '0';
            remainder = (remainder * 10 + currentDigit) % 7;
        }

        return remainder == 0;
    }

    string largestPalindrome(int n, int k) 
    {
        if (k == 1 || k == 3 || k == 9)
        {
            string ans = "";

            for (int i = 0; i < n; i++)
                ans += '9';

            return ans;
        }

        if (k == 2)
        {
            string ans = "8";

            for (int i = 1; i < n - 1; i++)
                ans += '9';

            if (n > 1)
                ans += '8';

            return ans;
        }

        if (k == 4)
        {
            if (n == 1)
                return "8";

            if (n == 2)
                return "88";

            if (n == 3)
                return "888";

            string ans = "88";

            for (int i = 2; i < n - 2; i++)
                ans += '9';

            ans += '8', ans += '8';

            return ans;
        }

        if (k == 5)
        {
            string ans = "5";

            for (int i = 1; i < n - 1; i++)
                ans += '9';

            if (n > 1)
                ans += '5';

            return ans;
        }

        if (k == 6)
        {
            if (n == 1)
                return "6";
            if (n == 2)
                return "66";

            int m = n / 2;

            if (n % 2 == 0)
            {
                string ans = "8";

                for (int i = 1; i < m - 1; i++)
                    ans += '9';

                ans += '7';

                string reverseAns = ans;
                reverse(reverseAns.begin(), reverseAns.end());

                return ans + reverseAns;
            }
            else
            {
                string ans = "8";

                for (int i = 1; i < m; i++)
                    ans += '9';

                ans += '8';

                for (int i = m + 1; i < n - 1; i++)
                    ans += '9';

                ans += '8';

                return ans;
            }
        }


        if (k == 7)
        {
            if (n == 1)
                return "7";
            if (n == 2)
                return "77";

            vector<char> ansList(n, '9');

            if (n % 2 == 0)
            {
                for (int digit = '9'; digit >= '0'; digit--)
                {
                    ansList[n / 2 - 1] = digit;
                    ansList[n / 2] = digit;

                    if (is7divisible(ansList))
                    {
                        string ans = "";

                        for (char c: ansList)
                            ans += c;

                        return ans;
                    }
                }
            }
            else
            {
                for (int digit = '9'; digit >= '0'; digit--)
                {
                    ansList[n / 2] = digit;

                    if (is7divisible(ansList))
                    {
                        string ans = "";

                        for (char c: ansList)
                            ans += c;

                        return ans;
                    }
                }
            }
        }


        if (k == 8)
        {
            if (n <= 6)
            {
                string ans = "";

                for (int i = 0; i < n; i++)
                    ans += '8';

                return ans;
            }

            string ans = "888";

            for (int i = 3; i < n - 3; i++)
                ans += '9';

            ans += '8', ans += '8', ans += '8';

            return ans;
        }

        return "";
    }
};
