class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        } else if (k == 2) {
            string ans = "8";
            for(int i = 1; i < n - 1; ++i) ans += '9';
            if (n > 1) ans += '8';
            return ans;
        } else if (k == 5) {
            string ans = "5";
            for(int i = 1; i < n - 1; ++i) ans += '9';
            if (n > 1) ans += '5';
            return ans;
        } else if (k == 4) {
            if (n <= 4) return string(n, '8');
            string ans = "88";
            for(int i = 0; i < n - 4; ++i) ans += '9';
            ans += "88";
            return ans;
        } else if (k == 6) {
            if (n <= 2) return string(n, '6');
            string ans = "8";
            for(int i = 0; i < (n - 3) / 2; ++i) ans += '9';
            if (n % 2) ans += '8';
            else ans += "77";
            for(int i = 0; i < (n - 3) / 2; ++i) ans += '9';
            ans += '8';
            return ans;
        } else if (k == 8) {
            if (n <= 6) return string(n, '8');
            string ans = "888";
            for(int i = 0; i < n - 6; ++i) ans += '9';
            ans += "888"; 
            return ans;
        } else if (k == 7) {
            if (n <= 2) return string(n, '7');
            vector<int> mid = {5, 77, 7, 99, 4, 44, 6, 44, 4, 99, 7, 77};
            string ans;
            for(int i = 0; i < (n - 1) / 2; ++i) ans += '9';
            ans += to_string(mid[(n - 3) % 12]);
            for(int i = 0; i < (n - 1) / 2; ++i) ans += '9';
            return ans;
        }
        return {};
    }
};
