class Solution {
public:
//  7 
    string largestPalindrome(int n, int k) {
        if(k==1 || k==3 || k==9)return string(n,'9');
        if(k==2){
            if(n==1)return "8";
            if(n==2)return "88";
            string s=string(n,'9');
            s[0]='8';
            s[n-1]='8';
            return s;
        }
        if(k==4){
            if(n==1)return "8";
            if(n==2)return "88";
            string s=string(n,'9');
            s[0]='8';
            s[1]='8';
            s[n-1]='8';
            s[n-2]='8';
            return s;
        }
        if(k==8){
            if(n<=6)return string(n,'8');
            string s=string(n,'9');
            s[0]='8';
            s[1]='8';
            s[2]='8';
            s[n-1]='8';
            s[n-2]='8';
            s[n-3]='8';
            return s;
        }
        if(k==5){
            string s=string(n,'9');
            s[0]='5';
            s[n-1]='5';
            return s;
        }
        if(k==6){
            if(n==1) return "6";
            if(n==2) return "66";
            string s=string(n,'9');
            s[0]='8';
            s[n-1]='8';
            if(n%2==0){
                s[n/2]='7';
                s[n/2-1]='7';
            }
            else s[n/2]='8';
            return s;
        }
        if(k==7){
            if(n==1) return "7";
            if(n==2) return "77";
            string s=string(n,'9');
            if(n%6==0)return s;
            if(n%12<6){
                if(n%3==0){
                    s[n/2]='5';
                    return s;
                }
                if((n%6)%2==0){
                    s[n/2]='7';
                    s[n/2-1]='7';
                }
                else s[n/2]='7';
                return s;
            }
            else{
                if(n%3==0){
                    s[n/2]='6';
                    return s;
                }
                if((n%6)%2==0){
                    s[n/2]='4';
                    s[n/2-1]='4';
                }
                else s[n/2]='4';
                return s;
            }
        }
        return "";
    }
};
