class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n <= 5:
            for i in range(10 ** n - 1, 0, -1):
                if i % k == 0:
                    s = str(i)
                    if s == s[::-1]:
                        return s
        f = [9] * n
        t = [1] * n
        v = 1
        for i in range(n - 1, -1, -1):
            t[i] = v % k
            v = v * 10 % k
        v = 0
        for i in range(n):
            v = (v * 10 + f[i]) % k
        if v == 0:
            return "".join(map(str, f))
        s = [[0, 0] for i in range(k)]
        s[v] = [1, -1, -1]
        for i in range(n // 2, n):
            j = n - 1 - i
            v = t[i]
            if i != j:
                v = (v + t[j]) % k
            if v == 0:
                continue
            for y in range(k):
                for x in range(k):
                    if s[x][0] and s[(x - v) % k][0] == 0:
                        s[(x - v) % k] = [1, x, i]
            if s[0][0]:
                break
        z = 0
        # print(s, t, f)
        while s[z][2] != -1:
            p, x, i = s[z]
            f[i] -= 1
            j = n - 1 - i
            if i != j:
                f[j] -= 1
            z = x
        return "".join(map(str, f))
