class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def div7(num):
            r = 0
            for d in num:
                d = int(d)
                r = (r * 10 + d) % 7
            return r % 7 == 0

        if k == 1 or k == 3 or k == 9:
            return '9' * n
        elif k == 2:
            if n == 1:
                return '8'
            return '8' + '9' * (n - 2) + '8'
        elif k == 4:
            if n == 1:
                return '8'
            if n == 2:
                return '88'
            if n == 3:
                return '888'
            return '88' + '9' * (n - 4) + '88'
        elif k == 5:
            if n == 1:
                return '5'
            return '5' + '9' * (n - 2) + '5'
        elif k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n == 3:
                return '888'
            if n % 2 == 0:
                return '8' + '9' * (n // 2 - 2) + '77' + '9' * (n // 2 - 2) + '8'
            else:
                return '8' + '9' * (n // 2 - 1) + '8' + '9' * (n // 2 - 1) + '8'
        elif k == 7:
            if n == 1:
                return '7'
            if n == 2:
                return '77'
            if n % 2 == 0:
                curr = 9
                while curr >= 0:
                    num = '9' * (n // 2 - 1) + str(curr) * 2 + '9' * (n // 2 - 1)
                    if div7(num):
                        return str(num)
                    curr -= 1
            else:
                curr = 9
                while curr >= 0:
                    num = '9' * (n // 2) + str(curr) + '9' * (n // 2)
                    if div7(num):
                        return str(num)
                    curr -= 1
        elif k == 8:
            if n == 1:
                return '8'
            if n == 2:
                return '88'
            if n == 3:
                return '888'
            if n == 4:
                return '8888'
            if n == 5:
                return '88888'
            if n == 6:
                return '888888'
            return '888' + '9' * (n - 6) + '888'
        return ''
