class Solution {
    public String largestPalindrome(int n, int k) {
        StringBuffer sb = new StringBuffer();
        if (k == 1 || k == 3 || k == 9) {
            return generateString('9', n);
        }
        else if (k == 2) {
            if (n == 1) {
                return "8";
            }
            else {
                return "8" + generateString('9', n - 2) + "8";
            }
        }
        else if (k == 4) {
            if (n <= 4) {
                return generateString('8', n);
            }
            else {
                return "88" + generateString('9', n - 4) + "88";
            }
        }
        else if (k == 5) {
            if (n <= 2) {
                return generateString('5', n);
            }
            else {
                return "5" + generateString('9', n - 2) + "5";
            }
        }
        else if (k == 6) {
            if (n == 1) {
                return "6";
            }
            else if (n == 2) {
                return "66";
            }
            else if (n == 3) {
                return "888";
            }
            else if (n % 2 == 0) {
                return "8" + generateString('9', (n - 4) / 2) + "77" + generateString('9', (n - 4) / 2) + "8";
            }
            else {
                return "8" + generateString('9', (n - 3) / 2) + "8" + generateString('9', (n - 3) / 2) + "8";
            }
        }
        else if (k == 7) {
            String[] tmp = new String[]{"","7","77","959","9779","99799","999999","9994999","99944999","999969999","9999449999","99999499999"};
            return generateString('9', n / 12 * 6) + tmp[n % 12] + generateString('9', n / 12 * 6);
        }
        else {
            if (n <= 6) {
                return generateString('8', n);
            }
            else {
                return "888" + generateString('9', n - 6) + "888";
            }
        }
    }

    private String generateString(char c, int cnt) {
        StringBuffer sb = new StringBuffer();
        while (cnt > 0) {
            sb.append(c);
            cnt--;
        }
        return sb.toString();
    }
}
