class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return n*"9"
        if k == 2:
            if n == 1:
                return "8"
            else:
                return "8" + (n-2)*"9" + "8"
        if k == 3:
            return n*"9"
        if k == 4:
            if n == 1:
                return "8"
            if n == 2:
                return "88"
            if n == 3:
                return "888"
            return "88" + (n-4)*"9" + "88"
        if k == 5:
            if n == 1:
                return "5"
            return "5" + (n-2)*"9" + "5"
        if k == 6:
            if n == 1:
                return "6"
            if n == 2:
                return "66"
            ans = [""]*n
            ans[0] = "8"
            ans[-1] = "8"
            total_digit_sum = 0
            for i in range(len(ans)):
                if i == 0 or i == len(ans)-1:
                    ans[i] = "8"
                    total_digit_sum += 8
                else:
                    ans[i] = "9"
                    total_digit_sum += 9
            if total_digit_sum%3 == 0:
                return "".join(ans)
            elif n%2 == 0:
                l = len(ans)//2-1
                r = len(ans)//2
                mod_value = total_digit_sum%3
                if mod_value == 1:
                    ans[l] = "7"
                    ans[r] = "7"
                else:
                    ans[l] = "8"
                    ans[r] = "8"
            else:
                mid = len(ans)//2
                mod_value = total_digit_sum%3
                ans[mid] = str(9-mod_value)
            return "".join(ans)
        if k == 7:
            if n <= 2:
                return "7"*n
            coefficients = [1, 3, 2, 6, 4, 5]*(n//6 + 2)
            coefficients = coefficients[:n]
            ans = ["9"]*n

            total_sum = 0
            for i in range(len(ans)):
                total_sum += int(ans[i])*coefficients[i]
                if total_sum >= 7:
                    total_sum %= 7

            if total_sum%7 == 0:
                return "".join(ans)


            if n%2 == 0:
                l = len(ans)//2-1
                r = len(ans)//2
                while l >= 0 and r < len(ans):
                    mod_value = total_sum%7
                    coef_sum = coefficients[l]+coefficients[r]
                    for x in range(8,0 if l > 0 else -1,-1):
                        mod_value -= coef_sum
                        mod_value = mod_value%7
                        if mod_value == 0:
                            ans[l] = str(x)
                            ans[r] = str(x)
                            return "".join(ans[::-1])
            else: #n%2 == 1
                mod_value = total_sum%7
                l = len(ans)//2
                coef_sum = coefficients[l]
                for x in range(8,0 if l > 0 else -1,-1):
                    mod_value -= coef_sum
                    mod_value = mod_value%7
                    if mod_value == 0:
                        ans[l] = str(x)
                        #ans[r] = str(x)
                        return "".join(ans[::-1])
                #print('fail')

        if k == 8: ## 8889888
            if n <= 6:
                return "8"*n
            else:
                return "8"*3 + (n-6)*"9" + "8"*3
        if k == 9:
            return n * "9"
