class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans(n,'9');
        if(k==5){
            ans.front()=ans.back()='5';
        }else if(k==2){
            ans.front()=ans.back()='8';
        }else if(k==4){
            for(int i=0;i<min(n,2);i++)
                ans[i]=ans[n-i-1]='8';
        }else if(k==6){
            if(n<=2){
                ans.front()=ans.back()='6';
            }else if(n%2==1){
                ans.front()=ans[n/2]=ans.back()='8';
            }else{
                ans.front()=ans.back()='8';
                ans[n/2]=ans[(n-1)/2]='7';
            }
        }else if(k==8){
            for(int i=0;i<min(n,3);i++)
                ans[i]=ans[n-i-1]='8';
        }else if(k==7){
            array<int,6> pw10m7={1};
            for(int i=1;i<6;i++)
                pw10m7[i]=pw10m7[i-1]*10%7;
            int ansm7=(pw10m7[n%6]+6)%7;
            int m=n%2==1?1:11;
            m*=pw10m7[((n-1)/2)%6];
            m%=7;
            char tgt='9';
            while(ansm7!=0){
                ansm7+=7-m;
                ansm7%=7;
                tgt--;
            }
            ans[n/2]=ans[(n-1)/2]=tgt;
        }
        return ans;
    }
};
