using System.Text;

public class Solution
{

    private string Get(int[] ans)
    {
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < ans.Length; i++)
        {
            sb.Append(ans[i]);
        }
        return sb.ToString();
    }
    public string LargestPalindrome(int n, int k)
    {
        int[] ans = new int[n];
        Array.Fill(ans, 9);
        StringBuilder sb = new StringBuilder();
        if(k == 1)
        {
            return Get(ans);
        }
        if(k == 2)
        {
            ans[0] = ans[n - 1] = 8;
            return Get(ans);
        }
        if(k == 3 || k == 9)
        {
            return Get(ans);
        }
        if(k == 4)
        {
            if (n == 1) return "8";
            ans[0] = ans[1] = ans[n - 1] = ans[n - 2] = 8;
            return Get(ans);
        }
        if(k == 5)
        {
            ans[0] = ans[n - 1] = 5;
            return Get(ans);
        }
        if(k == 6)
        {
            if (n == 1) return "6";
            if (n == 2) return "66";
            ans[0] = ans[n - 1] = 8;
            if (n % 2 == 0)
            {
                for (int s = 9; s >= 0; s--)
                {
                    ans[n / 2] = ans[n / 2 - 1] = s;
                    int m = 0;
                    for (int i = 0; i < n; i++)
                    {
                        m = (m * 10 + ans[i]) % 6;
                    }
                    if (m == 0) return Get(ans);
                }
            }
            else
            {
                for (int s = 9; s >= 0; s--)
                {
                    ans[n / 2] = s;
                    int m = 0;
                    for (int i = 0; i < n; i++)
                    {
                        m = (m * 10 + ans[i]) % 6;
                    }
                    if (m == 0) return Get(ans);
                }
            }
            return Get(ans);
        }
        if(k == 7)
        {
            if (n == 1) return "7";
            if(n % 2 == 0)
            {
                for(int s = 9; s >= 0; s--)
                {
                    ans[n / 2] = ans[n / 2 - 1] = s;
                    int m = 0;
                    for(int i = 0; i < n; i++)
                    {
                        m = (m * 10 + ans[i]) % 7;
                    }
                    if (m == 0) return Get(ans);
                }
            }
            else
            {
                for (int s = 9; s >= 0; s--)
                {
                    ans[n / 2] = s;
                    int m = 0;
                    for (int i = 0; i < n; i++)
                    {
                        m = (m * 10 + ans[i]) % 7;
                    }
                    if (m == 0) return Get(ans);
                }
            }
        }
        if(k == 8)
        {
            if (n == 1) return "8";
            if (n == 2) return "88";
            ans[0] = ans[1] = ans[2] = ans[n - 1] = ans[n - 2] = ans[n - 3] = 8;
            return Get(ans);
        }
        return Get(ans);
    }
}
