import sys
sys.set_int_max_str_digits(0)
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:

        if k in [1,3,9]:
            ans = [9] * n
            return "".join(map(str, ans))
        if k == 5:
            ans = [9] * n
            ans[0] = ans[-1] = 5
            return "".join(map(str, ans))

        if k == 2:
            ans = [9] * n
            ans[0] = ans[-1] = 8
            return "".join(map(str, ans))

        if k == 4:
            ans = [9] * n
            if n >= 2:
                ans[0] = ans[1] = ans[-2] = ans[-1] = 8
            else:
                ans = [8] * n
            return "".join(map(str, ans))

        if k == 8:
            ans = [9] * n
            if n >= 3:
                ans[0] = ans[1] = ans[2] = ans[-3] = ans[-2] = ans[-1] = 8
            else:
                ans = [8] * n
            return "".join(map(str, ans))

        ans = int(10**n) - 1
        if k == 6:
            ans -= 1
            ans -= int(10**n) // 10



        diff = 0
        if n % 2 == 1:
            diff = int(10**(n//2))
        else:
            diff =  int(10**((n-1)//2))
            diff *= 11
        # print(ans, diff, k, n)
        for i in range(10):
            if ans % k == 0:
                return str(ans)
            ans -= diff
        return str(ans)
