class Solution {
public:
string largestPalindrome(int n, int k) {
    string s = "";
    for (int i = 0; i < n; i++)
    {
        s.push_back('9');
    }
    if (k==1 || k==9 || k==3)
    {
        return s;
    }
    else if (k==2)
    {
        s[n-1] = '8';
        s[0] = '8';
        return s;
    }
    else if (k==4)
    {
        s[n-1] = '8';
        s[0] = '8';
        if (n>=3)
        {
            s[n-2] = '8';
            s[1] = '8';
        }
        return s;
    }
    else if (k==8)
    {
        s[n-1] = '8';
        s[0] = '8';
        if (n>=3)
        {
            s[n-2] = '8';
            s[1] = '8';
        }
        if (n>=5)
        {
            s[n-3] = '8';
            s[2] = '8';
        }
        return s;
    }
    else if (k==5)
    {
        s[0] = '5';
        s[n-1] = '5';
        return s;
    }
    else if (k==6)
    {
        if (n==1)
        {
            return "6";
        }
        else if (n==2)
        {
            return "66";
        }
        s[0] = '8';
        s[n-1] = '8';
        if (n%2==1)
        {
            s[n/2] = '8';
        }
        else
        {
            s[n/2] = '7';
            s[n/2-1] = '7';
        }
        return s;
    }
    else
    {
        while (1)
        {
            long long sum{0},x{0},y;
            string a;
            for (int i = s.length() - 1; i >= 0; )
            {
                int j = i;
                a = "";
                for (; j >= 0 && j >= i-2; j--)
                {
                    a.push_back(s[j]);
                }
                i = j;
                reverse(a.begin(),a.end());
                y = stoi(a);
                if (x==0)
                {
                    sum += y;
                    x = 1;
                }
                else
                {
                    sum -= y;
                    x = 0;
                }

            }
            if (x==0)
            {
                sum = -sum;
            }
            if (sum%7==0)
            {
                break;
            }
            if (n%2==1)
            {
                s[n/2]--;
            }
            else
            {
                s[n/2]--;
                s[n/2-1]--;
            }

        }
        return s;
    }
    return s;
}
};
