class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def calc(n):
            if n == 1:
                return "7"
            largest = 10 ** n - 1
            half = largest // 10**(n//2)

            def converts(n):
                l = []
                while n > 0:
                    l.append(str(n%10))
                    n //= 10
                return l[::-1]

            def converti(s):
                ret = 0
                for c in s:
                    ret = ret * 10 + int(c)
                return ret

            def make_palindrome(half, n):
                half_str = converts(half)
                if n % 2 == 0:
                    return half_str + half_str[::-1]
                else:
                    return half_str + half_str[:-1][::-1]

            for i in range(half, 0, -1):
                palindrome = make_palindrome(i, n)
                if converti(palindrome) % 7 == 0:
                    return palindrome

            return None

        ans = ["9" for i in range(n)]
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        elif k == 2:
            ans[0] = ans[-1] = "8"
            for i in range(1, n-1):
                ans[i] = "9"
        elif k == 4 or k == 8:
            ans[0] = ans[-1] = "8"
            if n > 1:
                ans[1] = ans[-2] = "8"
            for i in range(2, n-2):
                ans[i] = "9"
            if n > 2 and k == 8:
                ans[2] = ans[-3] = "8"
        elif k == 6:
            l = [9 for i in range(n)]
            l[0] = l[-1] = 8
            while sum(l) % 3 != 0:
                l[n//2] -= 1
                if n % 2 == 0:
                    l[n//2-1] -= 1
            ans = [str(l[i]) for i in range(n)]
        elif k == 5:
            ans[0] = ans[-1] = "5"
        elif k == 7:
            ans = calc(n)
        return ''.join(ans)
