class Solution {
public:
    string largestPalindromeDivisibleBy7(int n,string ans) {
    if (n == 1) return "7";
    else if (n == 2) return "77";
    else if (n == 3) return "959";
    else if (n == 4) return "9779";
    else if (n == 5) return "99799";
    else if (n == 6) return "999999";
    else if (n == 7) return "9994999";
    else if (n == 8) return "99944999";
    else if (n == 9) return "999969999";
    else if (n == 10) return "9999449999";
    else if (n == 11) return "99999499999";
    else if (n == 12) return "999999999999";
    else if (n == 13) return "9999997999999";
    else if (n == 14) return "99999977999999";
    else if (n == 15) return "999999959999999";
    else if (n == 16) return "9999999779999999";
    else if (n == 17) return "99999999799999999";
    else if (n == 18) return "999999999999999999";
    else {
        if(n%6==0) return ans;
        else{
            if((n/6)%2==0){
                if(n%6==1||n%6==5){
                    ans[n/2]='7';
                    return ans;
                }
                else if(n%6==2||n%6==4){
                    ans[n/2]='7';
                    ans[n/2-1]='7';
                    return ans;
                }
                 else{
                    ans[n/2]='5';
                    return ans;
                }

            }
            else{
                 if(n%6==1||n%6==5){
                    ans[n/2]='4';
                    return ans;
                }
                else if(n%6==2||n%6==4){
                    ans[n/2]='4';
                    ans[n/2-1]='4';
                    return ans;
                }
                 else{
                    ans[n/2]='6';
                    return ans;
                }

            }
        }
    }
}

    string largestPalindrome(int n, int k) {
        string ans="";
        for(int i=0;i<n;i++) ans+="9";
        if(k==1){
            return ans;
        }
        else if(k==2){
            ans[0]='8';
            ans[n-1]='8';
            return ans;
        }
        else if(k==3){
            return ans;
        }
        else if(k==4){
            if(n==1) return "8";
            ans[0]='8';
            ans[1]='8';
            ans[n-1]='8';
            ans[n-2]='8';
            return ans;
        }
        else if(k==5){
            ans[0]='5';
            ans[n-1]='5';
            return ans;
        }
        else if(k==6){
            if(n==1) return "6";
            else if(n==2) return "66";
            else if(n==3) return "888";
            else {
                if(n%2==1){
                ans[0]=ans[n-1]='8';
                ans[n/2]='8';
                return ans;
                }
                else{
                    ans[0]=ans[n-1]='8';
                ans[n/2]=ans[n/2-1]='7';
                return ans;
                }
            }
        }
        else if(k==7){
            return largestPalindromeDivisibleBy7(n,ans);
        }

        else if(k==8){
            if(n==3) return "888";
            else if(n==2) return "88";
            else if(n==1) return "8";
            else{
                ans[0]=ans[n-1]=ans[1]=ans[n-2]=ans[2]=ans[n-3]='8';
                return ans;
            }
        }

        else if(k==9){
            return ans;
        }

        return "";

    }
};
