class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        ans = ['0']*n
        # # 枚举每一位上选哪些数字
        # # 判断是否能被k整除
        # # 从大到小枚举
        def dfs(i):
            if i == n//2+1:
                # 枚举结束
                if int(''.join(ans)) % k == 0:
                    return True
                return False
            for j in range(9, -1, -1):
                ans[i] = str(j)
                ans[n-1-i] = str(j)
                if dfs(i+1):
                    return True
        # dfs(0)
        if k == 1 or k == 3 or k == 9:
            return '9'*n
        elif k == 2:
            if n > 1:
                return '8' + '9'*(n-2) + '8'
            else:
                return '8'
        elif k == 4:
            if n <= 4:
                return '8'*n
            else:
                return '88' + '9'*(n-4) + '88'
        elif k == 5:
            if n > 1:
                return '5' + '9'*(n-2) + '5'
            else:
                return '5'
        elif k == 6:
            if n <= 2:
                return '6'*n
            else:
                return '8' + '9'*((n-1)//2-1) + '8' +'9'*((n-1)//2-1) +'8' if n&1 else '8' + '9'*((n-1)//2-1) + '77' +'9'*((n-1)//2-1) +'8'
        elif k == 7:
            # 12为一周期
            r = n%12
            h = n//12
            if r==1:
                return '9'*(h*6) + '7' + '9'*(h*6)
            elif r==2:
                return '9'*(h*6) + '77' + '9'*(h*6)
            elif r==3:
                return '9'*(h*6) + '959' + '9'*(h*6)
            elif r==4:
                return '9'*(h*6) + '9779' + '9'*(h*6)
            elif r==5:
                return '9'*(h*6) + '99799' + '9'*(h*6)
            elif r==6:
                return '9'*(h*6) + '999999' + '9'*(h*6)
            elif r==7:
                return '9'*(h*6) + '9994999' + '9'*(h*6)
            elif r==8:
                return '9'*(h*6) + '99944999' + '9'*(h*6)
            elif r==9:
                return '9'*(h*6) + '999969999' + '9'*(h*6)
            elif r == 10:
                return '9'*(h*6) + '9999449999' + '9'*(h*6)
            elif r==11:
                return '9'*(h*6) + '99999499999' + '9'*(h*6)
            elif r==0:
                return '9'*n
        elif k == 8:
            if n <= 6:
                return '8'*n
            else:
                return '888' + '9'*(n-6) + '888'
