class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return '9' * n
        elif k == 2:
            if n == 1:
                return '8'
            return '8' + '9' * (n - 2) + '8'
        elif k == 4:
            if n <= 3:
                return '8' * n
            return '88' + '9' * (n - 4) + '88'
        elif k == 8:
            if n <= 6:
                return '8' * n
            return '888' + '9' * (n - 6) + '888'
        elif k == 5:
            if n == 1:
                return '5'
            return '5' + '9' * (n - 2) + '5'
        elif k == 6:
            if n <= 2:
                return '6' * n
            if n & 1:
                return '8' + '9' * (n - 3 >> 1) + '8' + '9' * (n - 3 >> 1) + '8'
            else:
                return '8' + '9' * (n - 4 >> 1) + '77' + '9' * (n - 4 >> 1) + '8'
        elif k == 7:
            if n <= 2:
                return '7' * n
            r = 0
            for i in range((n + 1) // 2 - 1):
                r += (9 * pow(10, n-1-i, 7) + 9 * pow(10, i, 7)) % 7
                print(9 * pow(10, n-1-i, 7) + 9 * pow(10, i, 7))

            if n & 1:
                for i in range(9, -1, -1):
                    if (r + i * pow(10, n // 2, 7)) % 7 == 0:
                        return '9' * (n - 1 >> 1) + str(i) + '9' * (n - 1 >> 1)
            else:
                for i in range(9, -1, -1):
                    if (r + i * pow(10, n // 2, 7) + i * pow(10, n // 2-1, 7)) % 7 == 0:
                        return '9' * (n - 2 >> 1) + str(i) * 2 + '9' * (n - 2 >> 1)
