class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(100000)
        if k == 1 or k == 3 or k == 9:
            return n * "9"
        elif k == 2:
            if n <= 1:
                return n * "8"
            else:
                return "8" + ((n - 2) * "9") + "8"
        elif k == 4:
            if n <= 3:
                return n * "8"
            else:
                return "88" + ((n - 4) * "9") + "88"
        elif k == 8:
            if n <= 5:
                return n * "8"
            else:
                return "888" + ((n - 6) * "9") + "888"
        elif k == 5:
            if n <= 1:
                return "5"
            else:
                return "5" + ((n - 2) * "9") + "5"
        elif k == 6:
            if n <= 2:
                return n * "6"
            else:
                if n % 2 == 0:
                    return "8" + ((n // 2 - 2) * "9") + "77" + ((n // 2 - 2) * "9") + "8"
                else:
                    return "8" + ((n // 2 - 1) * "9") + "8" + ((n // 2 - 1) * "9") + "8"
        elif k == 7:
            if n <= 2:
                return n * "7"
            else:
                for i in reversed(range(10)):
                    s = (((n - 1) // 2) * "9") + ((1 if n % 2 == 1 else 2) * str(i)) + (((n - 1) // 2) * "9")
                    if int(s) % 7 == 0:
                        return s
            return ""
