class Solution {
public:
    string largestPalindrome(int n, int k) {
        vector<int> pw(n);
        pw[0] = 1 % k;
        for(int i = 1; i < n; i++){
            pw[i] = (10 * pw[i - 1]) % k;
        }
        vector<vector<int>> pre(k, vector<int>(k, 0));
        for(int i = 0; i < k; i++){
            for(int j = 0; j < k; j++){
                pre[i][j] = (i + j * 9) % k;
            }
        }
        vector<vector<bool>> seen(n, vector<bool>(k, 0));
        string ans(n, '9');
        int cur = 0;
        for(int i = 0; i < n; i++){
            cur = (cur + pw[i] * 9) % k;
        }
        vector<vector<int>> prv(n, vector<int>(k, -1));
        int x = (n - 1) / 2, y = n - 1 - x;
        for(int d = 9; d >= 10 - k; d--){
            int s = pw[x] + (x != y) * pw[y];
            s *= d;
            s %= k;
            if(prv[x][s] == -1){
                prv[x][s] = d;
            }
        }
        for(int i = x - 1; i >= 0; i--){
            int j = n - 1 - i;
            for(int it = 0; it < k; it++){
                if(prv[i + 1][it] == -1) continue;
                int c = (pw[i] + pw[j]); if (c >= k) c -= k;
                int t = c * 9 + it; t %= k;
                for(int d = 9; d >= 10 - k; d--){
                    if(prv[i][t] < d){
                        prv[i][t] = d;
                    }
                    t += k - c;
                    if(t >= k) t -= k;
                }
            }
        }
        int s = 0;
        for(int i = 0; i <= x; i++){
            int j = n - 1 - i;
            int d = prv[i][s];
            int ns = s - (d * (pw[i] + pw[j])); ns %= k; if(ns < 0) ns += k;
            ans[i] = ans[j] = d + '0';
            s = ns;
        }
        return ans;
    }
};
