class Solution(object):
    def largestPalindrome(self, n, k):
        """
        :type n: int
        :type k: int
        :rtype: str
        """
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        if k == 5:
            if n == 1:
                return "5"
            return "5" + "9" * (n - 2) + "5"
        if k == 2:
            if n == 1:
                return "8"
            return "8" + "9" * (n - 2) + "8"
        if k == 4:
            if n < 4:
                return "8" * n
            return "88" + "9" * (n - 4) + "88"
        if k == 8:
            if n < 6:
                return "8" * n
            return "888" + "9" * (n - 6) + "888"
        if k == 6:
            if n == 1:
                return "6"
            if n == 2:
                return "66"
            if n % 2 == 1:
                return "8" + "9" * ((n - 3) // 2) + "8" + "9" * ((n - 3) // 2) + "8"
            return "8" + "9" * ((n - 4) // 2) + "77" + "9" * ((n - 4) // 2) + "8"

        if n == 1:
            return "7"
        if n == 2:
            return "77"

        if n % 6 == 0:
            return "9" * n

        def is_divisible_by_7(s):
            number = s
            while len(number) > 2:
                last_digit = int(number[-1])
                rest_of_number = number[:-1]
                last_three_digits = int(rest_of_number[-3: ])
                new_last_three_digits = str(last_three_digits - 2 * last_digit)
                number = rest_of_number[: -3] + new_last_three_digits
            return int(number) % 7 == 0

        if n % 2 == 0:
            for x in range(9, -1, -1):
                s = "9" * ((n - 2) // 2) + str(x) * 2 + "9" * ((n - 2) // 2)
                if is_divisible_by_7(s):
                    return s

        for x in range(9, -1, -1):
            s = "9" * (n // 2) + str(x) + "9" * (n // 2)
            if is_divisible_by_7(s):
                return s
